overpassQueryStrings.dedicatedCyclewaysQuery:
  [out:json];
rel(999999);map_to_area->.region;
(
  way(area.region)["highway"~"cycleway"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"]["segregated"!="no"]["foot"!~"designated|yes"]["access"!~"^(private|permissive|no)$"];

  way(area.region)["cycleway"="track"]["segregated"!="no"]["foot"!~"designated|yes"]["access"!~"^(private|permissive|no)$"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];
  way(area.region)["cycleway:right"="track"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];
  way(area.region)["cycleway:left"="track"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];
  way(area.region)["cycleway:both"="track"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];

  // No longer includes painted lanes with painted buffer space (but no physical separator)
  // Eg: 110kph Princes Motorway and only paint: https://www.openstreetmap.org/way/5067683

  // Include painted lane ONLY if there is some sort of physical separator
  // These might include car parking/bollards/flex posts/bumps/planters etc
  // See https://wiki.openstreetmap.org/wiki/Key:cycleway:separation
  way(area.region)["cycleway"="lane"]["cycleway:separation"]["cycleway:separation"!~"^(no|solid_line|dashed_line|)$"];
  way(area.region)["cycleway:left"="lane"]["cycleway:left:separation"]["cycleway:right:separation"!~"^(no|solid_line|dashed_line|)$"];
  way(area.region)["cycleway:right"="lane"]["cycleway:right:separation"]["cycleway:right:separation"!~"^(no|solid_line|dashed_line|)$"];
  way(area.region)["cycleway:both"="lane"]["cycleway:both:separation"]["cycleway:both:separation"!~"^(no|solid_line|dashed_line|)$"];

  // Include paved paths designated for bicycles
  way(area.region)["highway=path"]["bicycle"="designated"]["segregated"!="no"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];

  // Include bicycle ramps
  way(area.region)["ramp:bicycle"="yes"];
);
out geom;

overpassQueryStrings.onRoadCycleLanesQuery:
[out:json];
