overpassQueryStrings.safeStreetsQuery:
  [out:json];
rel(999999);map_to_area->.region;
(
  // Include all roads with a specified speed limit of <= 30kmh (except pedestrian streets that may disallow biycles by default)
  way(area.region)["highway"~"^(motorway|trunk|primary|secondary|tertiary|unclassified|residential|living_street|service|pedestrian)$"]["access"!~"^(private|permissive|no)$"]["service"!~"^(driveway|parking_aisle)$"]["highway"!="pedestrian"]["maxspeed"~"^(5|10|15|20|25|30)$"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];

  // Include living/shared streets even if they don't have a speed limit, but not if they have a >=30 kmh speed limit
  // (living streets imply bicycle=yes)
  way(area.region)["highway"="living_street"]["maxspeed"!~"^(40|50|60|70|80)$"]["access"!~"^(private|permissive|no)$"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];

  // Include pedestrian streets tagged as explicitly allowing cycling as long as they have an
  // advistory speed limit <= 30kmh (or no limit marked)
  // Pedestrian streets don't imply bicycle=yes (and include George Street, Sydney)
  way(area.region)["highway"="pedestrian"]["bicycle"="yes"]["maxspeed"!~"^(40|50|60|70|80)$"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];
  way(area.region)["highway"="pedestrian"]["bicycle"="yes"]["maxspeed:advisory"~"^(5|10|15|20|25|30)$"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];
);
out geom;

overpassQueryStrings.sharedPathsQuery:
[out:json];
rel(999999);map_to_area->.region;
(
  // Exclude footpath cycling for equal comparisons across states, though you could argue
  // a legal cycling footpath is as valid as a shared path. I've added a note in the copy text that
  // footpath cycling is excluded for clarity.

  // Not all footpaths in legal juristictions are tagged with bicycle=yes as well so this would be
  // incomplete without additional filters by legal juristiction.

  // There may be false negatives here, but footpaths that allow cycling and aren't a shared path
  // are somewhat rare (and are possibly tagging errors) in non-footpath cycling states.
  // See overpass-turbo.eu/s/1FMP for these cases.

  // Include shared paths
  way(area.region)["highway"="cycleway"]["segregated"="no"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];
  way(area.region)["highway"="cycleway"][!"segregated"]["foot"~"yes|designated"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];

  // Include paved paths as long as they aren't shared with pedestrians
  way(area.region)["highway=path"]["bicycle"="designated"]["segregated"="yes"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];

  // Include pedestrian streets tagged as explicitly allowing cycling (eg. Martin Place, Sydney)
  way(area.region)["highway"="pedestrian"]["bicycle"="yes"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];

  way(area.region)["cycleway"="track"]["foot"~"designated|yes"]["access"!~"^(private|permissive|no)$"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];
  way(area.region)["cycleway"="track"]["highway"="footway"]["access"!~"^(private|permissive|no)$"]["surface"!~"^(dirt|gravel|unpaved|ground|compacted|fine_gravel|shells|rock|pebblestone|earth|grass|sand)$"]["mtb"!="yes"];
);
out geom;
